    enum class column_flag : uint
    {
        NotNull         = NOT_NULL_FLAG,            /* field can't be NULL */
        PriKey          = PRI_KEY_FLAG,             /* field is part of a primary key */
        UniqueKey       = UNIQUE_KEY_FLAG,          /* field is part of a unique key */
        MultipleKey     = MULTIPLE_KEY_FLAG,        /* field is part of a key */
        Blob            = BLOB_FLAG,                /* field is a blob */
        Unsigned        = UNSIGNED_FLAG,            /* field is unsigned */
        Zerofill        = ZEROFILL_FLAG,            /* field is zerofill */
        Binary          = BINARY_FLAG,
        Enum            = ENUM_FLAG,                /* field is an enum */
        AutoIncrement   = AUTO_INCREMENT_FLAG,      /* field is a autoincrement field */
        Timestamp       = TIMESTAMP_FLAG,           /* field is a timestamp */
        Set             = SET_FLAG,                 /* field is a set */
        NoDefaultValue  = NO_DEFAULT_VALUE_FLAG,    /* field doesn't have default value */
        OnUpdateNow     = ON_UPDATE_NOW_FLAG,       /* field is set to NOW on UPDATE */
        Num             = NUM_FLAG,                 /* field is num (for clients) */
        PartKey         = PART_KEY_FLAG,            /* Intern; Part of some key */
        Group           = GROUP_FLAG,               /* Intern: Group field */
        Unique          = UNIQUE_FLAG,              /* Intern: Used by sql_yacc */
    };
    using column_flags  = utl::simple_flags<column_flag>;

